-- Add recommended foods for Diabetes (Type 2)
INSERT INTO public.disease_foods (disease_id, food_name, benefits, frequency, how_much, preparation_method) VALUES
-- Using the Diabetes (Type 2) ID: 97f7e1bf-521d-4e3b-932b-2a98d6403231
('97f7e1bf-521d-4e3b-932b-2a98d6403231', 'Leafy Greens (Spinach, Methi)', 'Very low in carbohydrates and calories but high in fibre, vitamins and minerals; help slow the rise of blood sugar after meals.', 'daily', '1-2 cups cooked daily', 'Steam lightly or add to dal and curries. Avoid overcooking to preserve nutrients.'),
('97f7e1bf-521d-4e3b-932b-2a98d6403231', 'Oats', 'Higher fibre and nutrients than refined grains; cause a slower, smaller rise in blood glucose.', 'daily', '30-40g dry oats', 'Cook as porridge with water or low-fat milk. Avoid instant oats with added sugar.'),
('97f7e1bf-521d-4e3b-932b-2a98d6403231', 'Moong Dal', 'Provides protein and soluble fibre which improve satiety and help control blood sugar and cholesterol.', 'daily', '1 bowl cooked dal', 'Cook with turmeric and minimal oil. Can be made as soup or added to vegetable dishes.'),
('97f7e1bf-521d-4e3b-932b-2a98d6403231', 'Almonds', 'Healthy fats, protein and fibre; when used in small portions they can improve blood sugar control and heart health.', 'daily', '10-12 almonds', 'Soak overnight and eat in the morning. Avoid salted or roasted varieties.'),
('97f7e1bf-521d-4e3b-932b-2a98d6403231', 'Guava', 'Whole fruits provide fibre, vitamins and antioxidants. Low glycemic index fruit.', 'as_needed', '1 medium guava', 'Eat fresh with skin. Best consumed between meals, not after heavy meals.'),
('97f7e1bf-521d-4e3b-932b-2a98d6403231', 'Bitter Gourd (Karela)', 'Contains compounds that may help regulate blood sugar levels and improve insulin sensitivity.', 'weekly', '1/2 cup cooked', 'Stir-fry with minimal oil, or make juice (diluted). Remove seeds for less bitter taste.'),

-- Add recommended foods for Heart Disease - ID: bf320d64-1785-4844-94bf-d0f67031a763
('bf320d64-1785-4844-94bf-d0f67031a763', 'Walnuts', 'Rich in omega-3 fatty acids which may reduce risk of heart events. Help improve lipid profile.', 'daily', '4-5 walnut halves', 'Eat raw. Can be added to salads or morning oatmeal.'),
('bf320d64-1785-4844-94bf-d0f67031a763', 'Salmon/Fatty Fish', 'Rich in long-chain omega-3 fats (EPA, DHA) which may reduce risk of heart events.', 'weekly', '100-150g serving', 'Bake, grill or steam. Avoid deep frying.'),
('bf320d64-1785-4844-94bf-d0f67031a763', 'Barley', 'Soluble fibre helps lower LDL cholesterol and improve long-term heart health.', 'weekly', '1/2 cup cooked', 'Cook as soup, add to salads, or use in place of rice occasionally.'),
('bf320d64-1785-4844-94bf-d0f67031a763', 'Pomegranate', 'Rich in antioxidants that may help reduce oxidative stress and protect blood vessels.', 'weekly', '1/2 cup seeds', 'Eat fresh seeds. Avoid packaged juices with added sugar.'),
('bf320d64-1785-4844-94bf-d0f67031a763', 'Olive Oil', 'Unsaturated fats help lower LDL and raise HDL when they replace saturated and trans fats.', 'daily', '1-2 tablespoons', 'Use for salad dressing or light cooking. Do not use for high-heat frying.'),
('bf320d64-1785-4844-94bf-d0f67031a763', 'Flaxseeds', 'Provide omega-3 fatty acids and fibre that support cardiovascular health.', 'daily', '1-2 tablespoons ground', 'Grind fresh and add to smoothies, yogurt or chapati dough.'),

-- Add recommended foods for High Blood Pressure - ID: 07d44f19-56a6-4911-805d-cd5b3b2ee161
('07d44f19-56a6-4911-805d-cd5b3b2ee161', 'Banana', 'Rich in potassium which helps counteract some of the harmful effects of sodium on blood pressure.', 'daily', '1 medium banana', 'Eat fresh. Best as morning snack or post-workout.'),
('07d44f19-56a6-4911-805d-cd5b3b2ee161', 'Beetroot', 'Contains nitrates that may help relax blood vessels and lower blood pressure.', 'weekly', '1/2 cup cooked or juiced', 'Roast, boil, or juice. Pair with carrots for better taste.'),
('07d44f19-56a6-4911-805d-cd5b3b2ee161', 'Low-fat Curd', 'Provides calcium and protein; low-fat options support BP control.', 'daily', '1 cup', 'Eat plain or as buttermilk (chaas) with minimal salt.'),
('07d44f19-56a6-4911-805d-cd5b3b2ee161', 'Garlic', 'Contains compounds that may help relax blood vessels and improve circulation.', 'daily', '1-2 cloves', 'Crush and add to cooking. Can also be consumed raw in morning.'),
('07d44f19-56a6-4911-805d-cd5b3b2ee161', 'Bottle Gourd (Lauki)', 'Low in sodium, high in water content, supports hydration and blood pressure management.', 'weekly', '1 cup cooked', 'Cook as sabzi with minimal salt. Can also be made into juice.'),

-- Add recommended foods for Skin Problems - ID: e1b2ec54-c655-43d9-9e2a-6917f35ba73e
('e1b2ec54-c655-43d9-9e2a-6917f35ba73e', 'Carrots', 'Rich in beta-carotene and vitamin A which support collagen formation and protect skin cells.', 'daily', '1 medium carrot', 'Eat raw, juice, or lightly cook. Great in salads.'),
('e1b2ec54-c655-43d9-9e2a-6917f35ba73e', 'Amla (Indian Gooseberry)', 'Extremely high in vitamin C which supports collagen production and skin health.', 'daily', '1-2 amla or 20ml juice', 'Eat fresh, make juice, or consume as candy (murabba with less sugar).'),
('e1b2ec54-c655-43d9-9e2a-6917f35ba73e', 'Chia Seeds', 'Omega-3 fats have anti-inflammatory properties that may support skin health.', 'daily', '1 tablespoon', 'Soak in water for 15 min, add to smoothies or yogurt.'),
('e1b2ec54-c655-43d9-9e2a-6917f35ba73e', 'Sweet Potato', 'Rich in beta-carotene that converts to vitamin A, essential for skin repair.', 'weekly', '1/2 cup cooked', 'Bake, boil or mash. Avoid deep frying.'),
('e1b2ec54-c655-43d9-9e2a-6917f35ba73e', 'Papaya', 'Contains papain enzyme and vitamin C that support skin cell renewal.', 'weekly', '1 cup diced', 'Eat fresh. Can also be applied topically as face mask.'),

-- Add recommended foods for High Cholesterol - ID: f0bb180f-0797-42ff-8a47-68fca99e0f1e
('f0bb180f-0797-42ff-8a47-68fca99e0f1e', 'Oats', 'Soluble fibre (beta-glucan) helps bind cholesterol in the gut and lower LDL levels.', 'daily', '40g dry oats', 'Cook as porridge. Add fruits instead of sugar for sweetness.'),
('f0bb180f-0797-42ff-8a47-68fca99e0f1e', 'Psyllium Husk (Isabgol)', 'Soluble fibre that helps reduce cholesterol absorption when taken regularly.', 'daily', '1-2 teaspoons', 'Mix with water and drink before meals. Always drink with adequate water.'),
('f0bb180f-0797-42ff-8a47-68fca99e0f1e', 'Rajma (Kidney Beans)', 'High in fibre and plant protein that help improve cholesterol profile.', 'weekly', '1/2 cup cooked', 'Soak overnight, pressure cook. Prepare as curry with minimal oil.'),
('f0bb180f-0797-42ff-8a47-68fca99e0f1e', 'Soy Products', 'Plant sterols and isoflavones may help reduce LDL cholesterol.', 'weekly', '100g tofu or 1 cup soy milk', 'Include tofu in stir-fries or curries. Choose unsweetened soy milk.'),

-- Add recommended foods for Fatty Liver - ID: 781aa87a-9bc6-4985-a2b0-ef2a06be2021
('781aa87a-9bc6-4985-a2b0-ef2a06be2021', 'Green Tea', 'Contains antioxidants that may help reduce fat accumulation in the liver.', 'daily', '2-3 cups', 'Brew fresh. Avoid adding sugar. Best consumed between meals.'),
('781aa87a-9bc6-4985-a2b0-ef2a06be2021', 'Broccoli', 'Low in calories, high in fibre, may help with liver fat reduction.', 'weekly', '1 cup cooked', 'Steam or lightly stir-fry. Avoid heavy cream-based preparations.'),
('781aa87a-9bc6-4985-a2b0-ef2a06be2021', 'Millets (Ragi, Jowar)', 'High fibre content improves insulin sensitivity and reduces fat storage in liver.', 'weekly', 'Replace 2-3 meals with millet rotis', 'Make rotis or porridge. Mix with wheat flour initially if new to millets.'),
('781aa87a-9bc6-4985-a2b0-ef2a06be2021', 'Lemon', 'Contains vitamin C and compounds that may support liver detoxification.', 'daily', 'Juice of 1/2 lemon in warm water', 'Drink on empty stomach. Can also be added to salads.'),

-- Add recommended foods for PCOS - ID: ddf587eb-209f-4df6-99a8-b47905cc9659
('ddf587eb-209f-4df6-99a8-b47905cc9659', 'Cinnamon', 'May help improve insulin sensitivity and regulate blood sugar levels.', 'daily', '1/2-1 teaspoon', 'Add to tea, smoothies, or sprinkle on oatmeal.'),
('ddf587eb-209f-4df6-99a8-b47905cc9659', 'Fenugreek Seeds (Methi)', 'May help improve insulin sensitivity and regulate hormones.', 'daily', '1 teaspoon soaked seeds', 'Soak overnight in water, consume in morning. Can also add to cooking.'),
('ddf587eb-209f-4df6-99a8-b47905cc9659', 'Whole Eggs', 'Provide quality protein and healthy fats that support hormone balance.', 'daily', '1-2 eggs', 'Boil, poach or scramble with minimal oil. Avoid excessive frying.'),
('ddf587eb-209f-4df6-99a8-b47905cc9659', 'Nuts (Mixed)', 'Healthy fats and protein help maintain stable blood sugar and hormone production.', 'daily', 'Handful (20-25g)', 'Eat raw or soaked. Avoid salted or honey-roasted varieties.'),

-- Add recommended foods for Constipation - ID: f771bd4d-b4cb-47cc-9686-a6ec4ffbf5a5
('f771bd4d-b4cb-47cc-9686-a6ec4ffbf5a5', 'Papaya', 'Contains papain enzyme and fibre that promote bowel movements.', 'daily', '1 cup diced', 'Eat fresh, preferably in the morning. Ripe papaya is most effective.'),
('f771bd4d-b4cb-47cc-9686-a6ec4ffbf5a5', 'Prunes (Dried Plums)', 'Natural laxative effect due to sorbitol and fibre content.', 'daily', '3-5 prunes', 'Soak in water overnight and eat in morning. Drink the water too.'),
('f771bd4d-b4cb-47cc-9686-a6ec4ffbf5a5', 'Wheat Bran', 'Insoluble fibre that speeds intestinal transit time.', 'daily', '1-2 tablespoons', 'Add to atta for chapati or sprinkle on curd/oatmeal.'),
('f771bd4d-b4cb-47cc-9686-a6ec4ffbf5a5', 'Warm Water with Lemon', 'Stimulates digestion and promotes bowel movements.', 'daily', '1-2 glasses', 'Drink first thing in the morning on empty stomach.'),

-- Add recommended foods for Acid Reflux - ID: 05b01adc-0775-4630-b7b0-45e26d2ca343
('05b01adc-0775-4630-b7b0-45e26d2ca343', 'Oatmeal', 'Bland, non-acidic, absorbs stomach acid and provides sustained energy.', 'daily', '1 bowl', 'Cook with water or low-fat milk. Avoid adding acidic fruits.'),
('05b01adc-0775-4630-b7b0-45e26d2ca343', 'Ginger', 'Natural anti-inflammatory that may help soothe digestive discomfort.', 'daily', '1/2 inch piece', 'Add to tea, soups, or cooking. Avoid consuming in excess.'),
('05b01adc-0775-4630-b7b0-45e26d2ca343', 'Melons (Watermelon, Muskmelon)', 'Low acid, high water content, soothing for digestive system.', 'as_needed', '1 cup diced', 'Eat fresh between meals, not immediately after heavy meals.'),
('05b01adc-0775-4630-b7b0-45e26d2ca343', 'Brown Rice', 'Complex carbohydrate that absorbs acid and is easy to digest.', 'weekly', '1/2 cup cooked', 'Cook well and eat in small portions. Avoid eating late at night.'),

-- Add recommended foods for Iron-Deficiency Anemia - ID: ccc6549c-f3a9-4b69-92c9-246b87c04327
('ccc6549c-f3a9-4b69-92c9-246b87c04327', 'Palak (Spinach)', 'Rich in non-heme iron. Consume with vitamin C for better absorption.', 'daily', '1-2 cups cooked', 'Cook lightly with lemon juice. Pair with tomato-based dishes.'),
('ccc6549c-f3a9-4b69-92c9-246b87c04327', 'Black Chana (Kala Chana)', 'Good plant source of iron and protein.', 'weekly', '1/2 cup cooked', 'Soak overnight, pressure cook. Eat with lemon for vitamin C boost.'),
('ccc6549c-f3a9-4b69-92c9-246b87c04327', 'Jaggery (Gud)', 'Contains iron and can replace refined sugar in many preparations.', 'daily', '1-2 small pieces', 'Add to warm milk or use in traditional desserts. Choose organic jaggery.'),
('ccc6549c-f3a9-4b69-92c9-246b87c04327', 'Pomegranate', 'Contains iron and vitamin C which enhances iron absorption.', 'daily', '1/2 cup seeds or 1 glass juice', 'Eat fresh seeds or make juice at home. Avoid packaged juices.'),
('ccc6549c-f3a9-4b69-92c9-246b87c04327', 'Sesame Seeds (Til)', 'Rich in iron and calcium. A small quantity provides significant nutrition.', 'daily', '1-2 tablespoons', 'Add to salads, make til laddoo, or use tahini in cooking.');